<script setup lang="ts">
import { reactive, type PropType } from 'vue';

export interface StyleOption {
    value: string
    label: string
    children?: Array<StyleOption>
};

const props = defineProps({
    imageUrl: String,
    price: Number,
    styleOptions: Array as PropType<Array<StyleOption>>,
});

interface PageState {
    selectedStyle: string
    targetPrice: number
};

const pageState = reactive<PageState>({
    selectedStyle: '',
    targetPrice: 0
});

const emit = defineEmits(['selected', 'target']);

const onTargetPriceChange = () => {
    emit('target', pageState.targetPrice);
};

const onSelectedStyleChange = () => {
    emit('selected', pageState.selectedStyle);
};
</script>

<template>
    <a-divider>Product Info</a-divider>
    <div>
        <a-row>
            <a-col :span="12">
                <a-image :width="200" :src="imageUrl" style="padding-bottom: 16px;"
                    fallback="data:image/png;base64," />
            </a-col>
            <a-col :span="12">
                <a-form layout="vertical" :model="pageState">
                    <div>
                        <p>Current price: JPY {{  Intl.NumberFormat('ja-JP', { style: 'currency', currency: 'JPY' }).format(price || 0) }}</p>
                    </div>
                    <a-form-item label="Colour & Size" name="style">
                        <a-cascader v-model:value="pageState.selectedStyle" :options="styleOptions" expand-trigger="hover"
                            placeholder="Please select" @change="onSelectedStyleChange" />
                    </a-form-item>
                    <a-form-item label="Target Price (JPY)" name="target">
                        <a-input-number v-model:value="pageState.targetPrice" :min="0" :max="100000" :controls="false"
                            style="width: 100%" defaultValue="0" string-mode @change="onTargetPriceChange" />
                    </a-form-item>
                </a-form>
            </a-col>
        </a-row>
    </div>
</template>